package com.DecisionMakingStatements;

public class IfElseIfExample 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int var1=20;
		
		int var2=100;
		
		int var3=80;
		
		
		if(var1>var2&&var1>var3)
		{
			System.out.println(" the variable var1 is greater then var2 and var3");
			
		}
		else
		{
			if(var2>var3)
			{
				System.out.println("the variable var2 is greater then var3");
				
			}
			else
			{
				
				System.out.println("The variable var3 is greater then var1 and var2");
			}
		}
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
