package com.Loops;

public class ForLoops
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		for( int a=1;a<3; a++)
		{
			System.out.println(a);
		}
		for(int i=2; i<5; i++)
		{
			System.out.println(i+"Testing");
		}
		
		System.out.println( " ### "  );
		for(int  i=10; i>5; i--)
		{
			System.out.println(i);
			
		}
		System.out.println(" ====== ");
		for(int m=-23; m>-30; m=m-3)
		{
			System.out.println(m+" J meter");
		}

	}

}
