package com.Loops;

public class NestedLoopsExample
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		for(int i=10; i>5; i--)
		{
			for( int k=15; k>i; k--)
			{
				System.out.println(" Hannu");
			}
		}
		
		
		for( int i=8; i>5; i--)
		{
			for( int k=10;k>i; k--)
			{
				System.out.println(i);
			}
		}
		
		System.out.println(" @@@@@@@@ ");
		
		for( int i=25; i>15; i=i-5)
		{
			for( int h=35; h>i; h=h-5)
			{
				System.out.println( h);
			}
			System.out.println(i); 
		}
		
		System.out.println(" $$$$$$$ ");
		
		for( int i=100; i>50; i=i-10)
		{
			for(int k=110; k>i; k=k-10)
			{
				System.out.print(k+" ");
			}
			System.out.println();
		}
		
		System.out.println(" !!!!!!");
		
		for(int i=50; i>30; i=i-5)
		{
			for( int a=60; a>i; a=a-5)
			{
				System.out.println(" liveTech:"+a);
			}
		}
		
		System.out.println(" ^^^^^^^^^^");
		
		for(int a=150; a>140; a=a-5)
		{
			for( int b=160; b>a; b=b-5)
			{
				System.out.println(b);
			}
			System.out.println(a);
		}
		
		
		System.out.println(" ==========");
		
		for(int f=20;f<30; f=f+10)
		{
			for( int g=5;g<f; g=g+10)
			{
				System.out.println(" hannu");
			}
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
